+
+.skill-category li::before {
+    content: 'â–¹';
+    position: absolute;
+    left: 0;
+    color: var(--primary);
+}
+
+/* Skills Progress */
+.skills-progress h3 {
+    text-align: center;
+    margin-bottom: var(--spacing-xl);
+}
+
+.skill-bars {
+    max-width: 600px;
+    margin: 0 auto;
+}
+
+.skill {
+    margin-bottom: var(--spacing-lg);
+}
+
+.skill-info {
+    display: flex;
+    justify-content: space-between;
+    margin-bottom: var(--spacing-sm);
