/* ===================================
   MODERN PORTFOLIO STYLES
   =================================== */

/* CSS Variables */
:root {
    /* Light Theme Colors */
    --primary: #6366f1;
    --primary-light: #818cf8;
    --primary-dark: #4f46e5;
    --secondary: #06b6d4;
    --accent: #f59e0b;
    --success: #10b981;
    
    /* Neutral Colors */
    --white: #ffffff;
    --gray-50: #f9fafb;
    --gray-100: #f3f4f6;
    --gray-200: #e5e7eb;
    --gray-300: #d1d5db;
    --gray-400: #9ca3af;
    --gray-500: #6b7280;
    --gray-600: #4b5563;
    --gray-700: #374151;
    --gray-800: #1f2937;
    --gray-900: #111827;
    
    /* Background Colors */
    --bg-primary: var(--white);
    --bg-secondary: var(--gray-50);
    --bg-tertiary: var(--gray-100);
    
    /* Text Colors */
    --text-primary: var(--gray-900);
    --text-secondary: var(--gray-700);
    --text-tertiary: var(--gray-500);
    
    /* Border Colors */
    --border-light: var(--gray-200);
    --border-medium: var(--gray-300);
    
    /* Shadow Colors */
    --shadow-sm: 0 1px 2px 0 rgb(0 0 0 / 0.05);
    --shadow-md: 0 4px 6px -1px rgb(0 0 0 / 0.1), 0 2px 4px -2px rgb(0 0 0 / 0.1);
    --shadow-lg: 0 10px 15px -3px rgb(0 0 0 / 0.1), 0 4px 6px -4px rgb(0 0 0 / 0.1);
    --shadow-xl: 0 20px 25px -5px rgb(0 0 0 / 0.1), 0 8px 10px -6px rgb(0 0 0 / 0.1);
    
    /* Spacing */
    --spacing-xs: 0.5rem;
    --spacing-sm: 1rem;
    --spacing-md: 1.5rem;
    --spacing-lg: 2rem;
    --spacing-xl: 3rem;
    --spacing-2xl: 4rem;
    
    /* Border Radius */
    --radius-sm: 0.375rem;
    --radius-md: 0.5rem;
    --radius-lg: 0.75rem;
    --radius-xl: 1rem;
    --radius-2xl: 1.5rem;
    
    /* Transitions */
    --transition-fast: 0.15s ease;
    --transition-normal: 0.3s ease;
    --transition-slow: 0.5s ease;
}

/* Dark Theme */
[data-theme="dark"] {
    --bg-primary: var(--gray-900);
    --bg-secondary: var(--gray-800);
    --bg-tertiary: var(--gray-700);
    --text-primary: var(--gray-100);
    --text-secondary: var(--gray-300);
    --text-tertiary: var(--gray-400);
    --border-light: var(--gray-700);
    --border-medium: var(--gray-600);
}

/* Base Styles */
* {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
}

html {
    scroll-behavior: smooth;
    font-size: 16px;
}

body {
    font-family: 'Inter', -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, sans-serif;
    line-height: 1.6;
    color: var(--text-primary);
    background-color: var(--bg-primary);
    transition: background-color var(--transition-normal), color var(--transition-normal);
}

/* Typography */
h1, h2, h3, h4, h5, h6 {
    font-weight: 700;
    line-height: 1.2;
    margin-bottom: var(--spacing-sm);
}

h1 { font-size: 3.5rem; }
h2 { font-size: 2.5rem; }
h3 { font-size: 2rem; }
h4 { font-size: 1.5rem; }
h5 { font-size: 1.25rem; }
h6 { font-size: 1rem; }

p {
    margin-bottom: var(--spacing-sm);
    color: var(--text-secondary);
}

/* Container */
.container {
    max-width: 1200px;
    margin: 0 auto;
    padding: 0 var(--spacing-lg);
}

/* Header */
header {
    position: fixed;
    top: 0;
    left: 0;
    right: 0;
    z-index: 1000;
    background-color: rgba(255, 255, 255, 0.95);
    backdrop-filter: blur(10px);
    border-bottom: 1px solid var(--border-light);
    transition: all var(--transition-normal);
}

[data-theme="dark"] header {
    background-color: rgba(17, 24, 39, 0.95);
    border-bottom-color: var(--border-light);
}

header.scrolled {
    background-color: rgba(255, 255, 255, 0.98);
    box-shadow: var(--shadow-lg);
}

[data-theme="dark"] header.scrolled {
    background-color: rgba(17, 24, 39, 0.98);
}

nav {
    display: flex;
    justify-content: space-between;
    align-items: center;
    padding: var(--spacing-md) 0;
}

.logo {
    font-size: 1.5rem;
    font-weight: 800;
    color: var(--primary);
    text-decoration: none;
}

.nav-links {
    display: flex;
    list-style: none;
    gap: var(--spacing-lg);
}

.nav-links a {
    text-decoration: none;
    color: var(--text-primary);
    font-weight: 500;
    transition: color var(--transition-fast);
    position: relative;
}

.nav-links a:hover {
    color: var(--primary);
}

.nav-links a::after {
    content: '';
    position: absolute;
    bottom: -4px;
    left: 0;
    width: 0;
    height: 2px;
    background-color: var(--primary);
    transition: width var(--transition-normal);
}

.nav-links a:hover::after {
    width: 100%;
}

/* Theme Switch */
.theme-switch-wrapper {
    position: relative;
}

.theme-switch {
    display: inline-block;
    width: 60px;
    height: 34px;
    position: relative;
}

.theme-switch input {
    opacity: 0;
    width: 0;
    height: 0;
}

.slider {
    position: absolute;
    cursor: pointer;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background-color: var(--gray-300);
    transition: var(--transition-normal);
    border-radius: 34px;
}

.slider:before {
    position: absolute;
    content: "";
    height: 26px;
    width: 26px;
    left: 4px;
    bottom: 4px;
    background-color: white;
    transition: var(--transition-normal);
    border-radius: 50%;
}

input:checked + .slider {
    background-color: var(--primary);
}

input:checked + .slider:before {
    transform: translateX(26px);
}

.slider i {
    position: absolute;
    top: 50%;
    transform: translateY(-50%);
    font-size: 14px;
    color: var(--gray-600);
}

.slider .fa-sun {
    left: 8px;
}

.slider .fa-moon {
    right: 8px;
}

/* Hamburger Menu */
.hamburger {
    display: none;
    cursor: pointer;
    flex-direction: column;
    gap: 4px;
}

.bar {
    width: 25px;
    height: 3px;
    background-color: var(--text-primary);
    transition: var(--transition-normal);
}

/* Hero Section */
.hero {
    min-height: 100vh;
    display: flex;
    align-items: center;
    position: relative;
    overflow: hidden;
    background: linear-gradient(135deg, var(--bg-secondary) 0%, var(--bg-tertiary) 100%);
    padding-top: 80px;
}

.aurora-container {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    overflow: hidden;
    opacity: 0.6;
}

.aurora-1, .aurora-2, .aurora-3, .aurora-4 {
    position: absolute;
    width: 60%;
    height: 60%;
    border-radius: 50%;
    filter: blur(80px);
    animation: aurora-float 20s infinite alternate ease-in-out;
}

.aurora-1 {
    background: linear-gradient(90deg, var(--primary), var(--secondary));
    top: -20%;
    left: -20%;
    animation-duration: 25s;
}

.aurora-2 {
    background: linear-gradient(90deg, var(--accent), var(--primary));
    bottom: -20%;
    right: -20%;
    animation-duration: 30s;
    animation-delay: 2s;
}

.aurora-3 {
    background: linear-gradient(90deg, var(--secondary), var(--accent));
    top: 40%;
    right: -10%;
    animation-duration: 35s;
    animation-delay: 1s;
}

.aurora-4 {
    background: linear-gradient(90deg, var(--primary), var(--accent));
    bottom: 30%;
    left: -10%;
    animation-duration: 40s;
    animation-delay: 3s;
}

@keyframes aurora-float {
    0% {
        transform: translate(0, 0) scale(1) rotate(0deg);
    }
    100% {
        transform: translate(5%, 5%) scale(1.1) rotate(5deg);
    }
}

.stars-container {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background-image: 
        radial-gradient(2px 2px at 20% 30%, var(--gray-400) 1px, transparent 1px),
        radial-gradient(2px 2px at 40% 70%, var(--gray-400) 1px, transparent 1px),
        radial-gradient(2px 2px at 60% 20%, var(--gray-400) 1px, transparent 1px),
        radial-gradient(2px 2px at 80% 50%, var(--gray-400) 1px, transparent 1px),
        radial-gradient(2px 2px at 90% 30%, var(--gray-400) 1px, transparent 1px);
    background-size: 200px 200px;
    opacity: 0.3;
    animation: stars-move 30s linear infinite;
}

@keyframes stars-move {
    from { transform: translateY(0); }
    to { transform: translateY(-50%); }
}

.hero-content {
    position: relative;
    z-index: 2;
    width: 100%;
}

.hero-flex-container {
    display: flex;
    align-items: center;
    gap: var(--spacing-2xl);
    max-width: 1000px;
    margin: 0 auto;
}

.hero-image {
    flex-shrink: 0;
}

.image-container {
    position: relative;
    width: 280px;
    height: 280px;
}

.image-container img {
    width: 100%;
    height: 100%;
    object-fit: cover;
    border-radius: var(--radius-2xl);
    box-shadow: var(--shadow-xl);
    transition: transform var(--transition-normal);
}

.image-container:hover img {
    transform: scale(1.05);
}

.image-glow {
    position: absolute;
    top: -20px;
    left: -20px;
    right: -20px;
    bottom: -20px;
    background: linear-gradient(45deg, var(--primary), var(--secondary));
    border-radius: var(--radius-2xl);
    opacity: 0.3;
    filter: blur(20px);
    z-index: -1;
    animation: glow-pulse 3s ease-in-out infinite alternate;
}

@keyframes glow-pulse {
    0% { opacity: 0.2; transform: scale(1); }
    100% { opacity: 0.4; transform: scale(1.05); }
}

.hero-text {
    flex: 1;
}

.greeting {
    display: flex;
    align-items: center;
    gap: var(--spacing-sm);
    margin-bottom: var(--spacing-md);
}

.wave {
    font-size: 2rem;
    animation: wave 2s ease-in-out infinite;
}

@keyframes wave {
    0%, 100% { transform: rotate(0deg); }
    25% { transform: rotate(20deg); }
    75% { transform: rotate(-20deg); }
}

.greeting-text {
    font-size: 1.2rem;
    color: var(--text-secondary);
    font-weight: 500;
}

.name {
    font-size: 3.5rem;
    background: linear-gradient(135deg, var(--primary), var(--secondary));
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
    margin-bottom: var(--spacing-md);
    animation: fadeInUp 1s ease;
}

.title {
    font-size: 2rem;
    color: var(--text-primary);
    margin-bottom: var(--spacing-md);
    animation: fadeInUp 1s ease 0.2s both;
}

.txt-rotate {
    color: var(--primary);
    font-weight: 600;
}

.description {
    font-size: 1.2rem;
    color: var(--text-secondary);
    margin-bottom: var(--spacing-xl);
    animation: fadeInUp 1s ease 0.4s both;
}

.cta-buttons {
    display: flex;
    gap: var(--spacing-md);
    margin-bottom: var(--spacing-xl);
    animation: fadeInUp 1s ease 0.6s both;
}

.btn {
    display: inline-flex;
    align-items: center;
    gap: var(--spacing-sm);
    padding: var(--spacing-md) var(--spacing-lg);
    border-radius: var(--radius-lg);
    text-decoration: none;
    font-weight: 600;
    transition: all var(--transition-normal);
    border: none;
    cursor: pointer;
    font-size: 1rem;
}

.primary-btn {
    background: linear-gradient(135deg, var(--primary), var(--primary-light));
    color: var(--white);
    box-shadow: var(--shadow-lg);
}

.primary-btn:hover {
    transform: translateY(-2px);
    box-shadow: var(--shadow-xl);
}

.secondary-btn {
    background: transparent;
    color: var(--primary);
    border: 2px solid var(--primary);
}

.secondary-btn:hover {
    background: var(--primary);
    color: var(--white);
    transform: translateY(-2px);
}

.hero-stats {
    display: flex;
    gap: var(--spacing-xl);
    animation: fadeInUp 1s ease 0.8s both;
}

.stat {
    text-align: center;
}

.stat-number {
    display: block;
    font-size: 2rem;
    font-weight: 800;
    color: var(--primary);
}

.stat-label {
    font-size: 0.9rem;
    color: var(--text-tertiary);
    font-weight: 500;
}

/* Section Headers */
.section-header {
    text-align: center;
    margin-bottom: var(--spacing-2xl);
}

.section-title {
    font-size: 2.5rem;
    margin-bottom: var(--spacing-md);
    position: relative;
    display: inline-block;
}

.section-title::after {
    content: '';
    position: absolute;
    bottom: -10px;
    left: 50%;
    transform: translateX(-50%);
    width: 80px;
    height: 4px;
    background: linear-gradient(90deg, var(--primary), var(--secondary));
    border-radius: 2px;
}

.section-subtitle {
    font-size: 1.2rem;
    color: var(--text-secondary);
    max-width: 600px;
    margin: 0 auto;
}

/* Projects Section */
.projects {
    padding: var(--spacing-2xl) 0;
    background-color: var(--bg-secondary);
}

.project-filter {
    display: flex;
    justify-content: center;
    gap: var(--spacing-md);
    margin-bottom: var(--spacing-2xl);
    flex-wrap: wrap;
}

.filter-btn {
    padding: var(--spacing-sm) var(--spacing-lg);
    border: none;
    background-color: var(--bg-primary);
    color: var(--text-secondary);
    border-radius: var(--radius-lg);
    font-weight: 600;
    cursor: pointer;
    transition: all var(--transition-normal);
    box-shadow: var(--shadow-sm);
}

.filter-btn.active,
.filter-btn:hover {
    background: linear-gradient(135deg, var(--primary), var(--secondary));
    color: var(--white);
    transform: translateY(-2px);
    box-shadow: var(--shadow-lg);
}

.project-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(350px, 1fr));
    gap: var(--spacing-xl);
    margin-bottom: var(--spacing-2xl);
}

.project-card {
    background-color: var(--bg-primary);
    border-radius: var(--radius-xl);
    overflow: hidden;
    box-shadow: var(--shadow-md);
    transition: all var(--transition-normal);
    position: relative;
}

.project-card:hover {
    transform: translateY(-8px);
    box-shadow: var(--shadow-xl);
}

.project-img {
    position: relative;
    height: 250px;
    overflow: hidden;
}

.project-img img {
    width: 100%;
    height: 100%;
    object-fit: cover;
    transition: transform var(--transition-normal);
}

.project-card:hover .project-img img {
    transform: scale(1.1);
}

.project-overlay {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: rgba(0, 0, 0, 0.8);
    display: flex;
    align-items: center;
    justify-content: center;
    opacity: 0;
    transition: opacity var(--transition-normal);
}

.project-card:hover .project-overlay {
    opacity: 1;
}

.project-actions {
    display: flex;
    gap: var(--spacing-md);
}

.project-link,
.project-code {
    width: 50px;
    height: 50px;
    background-color: var(--white);
    border-radius: 50%;
    display: flex;
    align-items: center;
    justify-content: center;
    color: var(--primary);
    font-size: 1.2rem;
    transition: all var(--transition-normal);
    transform: translateY(20px);
}

.project-card:hover .project-link,
.project-card:hover .project-code {
    transform: translateY(0);
}

.project-link:hover,
.project-code:hover {
    background-color: var(--primary);
    color: var(--white);
    transform: translateY(-5px);
}

.project-badge {
    position: absolute;
    top: var(--spacing-md);
    right: var(--spacing-md);
    background: linear-gradient(135deg, var(--primary), var(--secondary));
    color: var(--white);
    padding: var(--spacing-xs) var(--spacing-sm);
    border-radius: var(--radius-lg);
    font-size: 0.8rem;
    font-weight: 600;
}

.project-info {
    padding: var(--spacing-lg);
}

.project-info h3 {
    font-size: 1.5rem;
    margin-bottom: var(--spacing-sm);
    color: var(--text-primary);
}

.project-info p {
    color: var(--text-secondary);
    margin-bottom: var(--spacing-md);
    line-height: 1.6;
}

.project-tags {
    display: flex;
    flex-wrap: wrap;
    gap: var(--spacing-sm);
}

.project-tags span {
    background-color: var(--bg-tertiary);
    color: var(--text-secondary);
    padding: var(--spacing-xs) var(--spacing-sm);
    border-radius: var(--radius-lg);
    font-size: 0.8rem;
    font-weight: 500;
}

.view-more {
    text-align: center;
}

/* About Section */
.about {
    padding: var(--spacing-2xl) 0;
    background-color: var(--bg-primary);
}

.about-content {
    max-width: 800px;
    margin: 0 auto;
}

.about-intro {
    margin-bottom: var(--spacing-2xl);
}

.about-intro p {
    font-size: 1.1rem;
    line-height: 1.8;
    margin-bottom: var(--spacing-md);
}

.skills-container {
    margin-bottom: var(--spacing-2xl);
}

.skills-container h3 {
    text-align: center;
    margin-bottom: var(--spacing-xl);
    color: var(--text-primary);
}

.skills-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
    gap: var(--spacing-lg);
    margin-bottom: var(--spacing-xl);
}

.skill-category {
    background-color: var(--bg-secondary);
    padding: var(--spacing-lg);
    border-radius: var(--radius-xl);
    box-shadow: var(--shadow-md);
    transition: transform var(--transition-normal);
}

.skill-category:hover {
    transform: translateY(-5px);
}

.skill-header {
    display: flex;
    align-items: center;
    gap: var(--spacing-sm);
    margin-bottom: var(--spacing-md);
}

.skill-header i {
    font-size: 1.5rem;
    color: var(--primary);
}

.skill-header h4 {
    color: var(--text-primary);
    margin-bottom: 0;
}

.skill-category ul {
    list-style: none;
}

.skill-category li {
    margin-bottom: var(--spacing-sm);
    padding-left: var(--spacing-md);
    position: relative;
    color: var(--text-secondary);
}

.skill-category li::before {
    content: 'â–¹';
    position: absolute;
    left: 0;
    color: var(--primary);
}

/* Skills Progress */
.skills-progress h3 {
    text-align: center;
    margin-bottom: var(--spacing-xl);
}

.skill-bars {
    max-width: 600px;
    margin: 0 auto;
}

.skill {
    margin-bottom: var(--spacing-lg);
}

.skill-info {
    display: flex;
    justify-content: space-between;
    margin-bottom: var(--spacing-sm);
    font-weight: 600;
}

.skill-name {
    color: var(--text-primary);
}

.skill-percentage {
    color: var(--primary);
}

.skill-bar {
    height: 12px;
    background-color: var(--bg-tertiary);
    border-radius: var(--radius-lg);
    overflow: hidden;
    box-shadow: inset var(--shadow-sm);
}

.skill-level {
    height: 100%;
    background: linear-gradient(90deg, var(--primary), var(--secondary));
    border-radius: var(--radius-lg);
    width: 0;
    transition: width 1.5s ease-out;
    position: relative;
}

.skill-level::after {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: linear-gradient(90deg, 
        rgba(255, 255, 255, 0.1) 20%, 
        rgba(255, 255, 255, 0.3) 50%, 
        rgba(255, 255, 255, 0.1) 80%);
    width: 200%;
    animation: shimmer 3s infinite linear;
}

@keyframes shimmer {
    0% { transform: translateX(-100%); }
    100% { transform: translateX(50%); }
}

/* Experience Timeline */
.experience h3 {
    text-align: center;
    margin-bottom: var(--spacing-xl);
}

.timeline {
    position: relative;
    max-width: 600px;
    margin: 0 auto;
}

.timeline::before {
    content: '';
    position: absolute;
    top: 0;
    left: 20px;
    height: 100%;
    width: 2px;
    background: linear-gradient(to bottom, var(--primary), var(--secondary));
}

.timeline-item {
    position: relative;
    padding-left: 60px;
    margin-bottom: var(--spacing-xl);
}

.timeline-marker {
    position: absolute;
    left: 11px;
    top: 0;
    width: 20px;
    height: 20px;
    background: var(--primary);
    border-radius: 50%;
    border: 4px solid var(--bg-primary);
    box-shadow: var(--shadow-md);
}

.timeline-content h4 {
    color: var(--text-primary);
    margin-bottom: var(--spacing-xs);
}

.company {
    display: block;
    color: var(--primary);
    font-weight: 600;
    margin-bottom: var(--spacing-xs);
}

.period {
    display: block;
    color: var(--text-tertiary);
    font-size: 0.9rem;
    margin-bottom: var(--spacing-sm);
}

/* Contact Section */
.contact {
    padding: var(--spacing-2xl) 0;
    background-color: var(--bg-secondary);
}

.contact-content {
    display: grid;
    grid-template-columns: 1fr 1fr;
    gap: var(--spacing-2xl);
    max-width: 1000px;
    margin: 0 auto;
}

.contact-info {
    display: flex;
    flex-direction: column;
    gap: var(--spacing-lg);
}

.contact-item {
    display: flex;
    align-items: center;
    gap: var(--spacing-md);
}

.contact-icon {
    width: 60px;
    height: 60px;
    background: linear-gradient(135deg, var(--primary), var(--secondary));
    border-radius: 50%;
    display: flex;
    align-items: center;
    justify-content: center;
    color: var(--white);
    font-size: 1.5rem;
    box-shadow: var(--shadow-md);
}

.contact-details h4 {
    color: var(--text-primary);
    margin-bottom: var(--spacing-xs);
}

.contact-details a,
.contact-details p {
    color: var(--text-secondary);
    text-decoration: none;
    transition: color var(--transition-fast);
}

.contact-details a:hover {
    color: var(--primary);
}

.social-links h4 {
    color: var(--text-primary);
    margin-bottom: var(--spacing-md);
}

.social-icons {
    display: flex;
    gap: var(--spacing-md);
}

.social-icon {
    width: 50px;
    height: 50px;
    background-color: var(--bg-primary);
    border-radius: 50%;
    display: flex;
    align-items: center;
    justify-content: center;
    color: var(--text-secondary);
    font-size: 1.2rem;
    transition: all var(--transition-normal);
    box-shadow: var(--shadow-sm);
}

.social-icon:hover {
    background: linear-gradient(135deg, var(--primary), var(--secondary));
    color: var(--white);
    transform: translateY(-3px);
    box-shadow: var(--shadow-lg);
}

/* Contact Form */
.contact-form {
    background-color: var(--bg-primary);
    padding: var(--spacing-xl);
    border-radius: var(--radius-xl);
    box-shadow: var(--shadow-lg);
}

.contact-form h3 {
    color: var(--text-primary);
    margin-bottom: var(--spacing-lg);
}

.form-group {
    position: relative;
    margin-bottom: var(--spacing-lg);
}

.form-group input,
.form-group textarea {
    width: 100%;
    padding: var(--spacing-md);
    border: 2px solid var(--border-light);
    border-radius: var(--radius-lg);
    background-color: var(--bg-primary);
    color: var(--text-primary);
    font-size: 1rem;
    transition: all var(--transition-normal);
}

.form-group textarea {
    resize: vertical;
    min-height: 120px;
}

.form-group input:focus,
.form-group textarea:focus {
    outline: none;
    border-color: var(--primary);
    box-shadow: 0 0 0 3px rgba(99, 102, 241, 0.1);
}

.form-group label {
    position: absolute;
    top: var(--spacing-md);
    left: var(--spacing-md);
    color: var(--text-tertiary);
    pointer-events: none;
    transition: all var(--transition-normal);
    background-color: var(--bg-primary);
    padding: 0 var(--spacing-xs);
}

.form-group input:focus + label,
.form-group textarea:focus + label,
.form-group input:not(:placeholder-shown) + label,
.form-group textarea:not(:placeholder-shown) + label {
    top: -10px;
    left: var(--spacing-sm);
    font-size: 0.8rem;
    color: var(--primary);
    font-weight: 600;
}

/* Footer */
footer {
    background-color: var(--bg-tertiary);
    padding: var(--spacing-2xl) 0 var(--spacing-lg);
    border-top: 1px solid var(--border-light);
}

.footer-content {
    display: flex;
    justify-content: space-between;
    align-items: center;
    margin-bottom: var(--spacing-xl);
    flex-wrap: wrap;
    gap: var(--spacing-lg);
}

.footer-logo h3 {
    color: var(--primary);
    margin-bottom: var(--spacing-sm);
}

.footer-logo p {
    color: var(--text-secondary);
    margin-bottom: 0;
}

.footer-links {
    display: flex;
    gap: var(--spacing-lg);
}

.footer-links a {
    color: var(--text-secondary);
    text-decoration: none;
    transition: color var(--transition-fast);
}

.footer-links a:hover {
    color: var(--primary);
}

.footer-social {
    display: flex;
    gap: var(--spacing-md);
}

.footer-bottom {
    display: flex;
    justify-content: space-between;
    align-items: center;
    padding-top: var(--spacing-lg);
    border-top: 1px solid var(--border-light);
    color: var(--text-tertiary);
    font-size: 0.9rem;
    flex-wrap: wrap;
    gap: var(--spacing-md);
}

.footer-bottom i {
    color: var(--primary);
    margin: 0 4px;
}

/* Animations */
@keyframes fadeInUp {
    from {
        opacity: 0;
        transform: translateY(30px);
    }
    to {
        opacity: 1;
        transform: translateY(0);
    }
}

/* Responsive Design */
@media (max-width: 1024px) {
    .hero-flex-container {
        flex-direction: column;
        text-align: center;
        gap: var(--spacing-xl);
    }
    
    .hero-stats {
        justify-content: center;
    }
    
    .contact-content {
        grid-template-columns: 1fr;
        gap: var(--spacing-xl);
    }
}

@media (max-width: 768px) {
    .nav-links {
        position: fixed;
        top: 80px;
        left: -100%;
        width: 100%;
        height: calc(100vh - 80px);
        background-color: var(--bg-primary);
        flex-direction: column;
        align-items: center;
        justify-content: center;
        transition: left var(--transition-normal);
        box-shadow: var(--shadow-lg);
    }
    
    .nav-links.active {
        left: 0;
    }
    
    .nav-links li {
        margin: var(--spacing-md) 0;
    }
    
    .hamburger {
        display: flex;
    }
    
    .hamburger.active .bar:nth-child(1) {
        transform: translateY(7px) rotate(45deg);
    }
    
    .hamburger.active .bar:nth-child(2) {
        opacity: 0;
    }
    
    .hamburger.active .bar:nth-child(3) {
        transform: translateY(-7px) rotate(-45deg);
    }
    
    .name {
        font-size: 2.5rem;
    }
    
    .title {
        font-size: 1.5rem;
    }
    
    .cta-buttons {
        flex-direction: column;
        align-items: center;
    }
    
    .hero-stats {
        flex-direction: column;
        gap: var(--spacing-lg);
    }
    
    .project-grid {
        grid-template-columns: 1fr;
    }
    
    .skills-grid {
        grid-template-columns: 1fr;
    }
    
    .footer-content {
        flex-direction: column;
        text-align: center;
    }
    
    .footer-bottom {
        flex-direction: column;
        text-align: center;
    }
}

@media (max-width: 480px) {
    .container {
        padding: 0 var(--spacing-md);
    }
    
    .name {
        font-size: 2rem;
    }
    
    .title {
        font-size: 1.3rem;
    }
    
    .description {
        font-size: 1rem;
    }
    
    .section-title {
        font-size: 2rem;
    }
    
    .image-container {
        width: 220px;
        height: 220px;
    }
    
    .btn {
        padding: var(--spacing-sm) var(--spacing-md);
        font-size: 0.9rem;
    }
}

/* Utility Classes */
.text-center { text-align: center; }
.text-left { text-align: left; }
.text-right { text-align: right; }

.mb-0 { margin-bottom: 0; }
.mb-1 { margin-bottom: var(--spacing-xs); }
.mb-2 { margin-bottom: var(--spacing-sm); }
.mb-3 { margin-bottom: var(--spacing-md); }
.mb-4 { margin-bottom: var(--spacing-lg); }
.mb-5 { margin-bottom: var(--spacing-xl); }

.mt-0 { margin-top: 0; }
.mt-1 { margin-top: var(--spacing-xs); }
.mt-2 { margin-top: var(--spacing-sm); }
.mt-3 { margin-top: var(--spacing-md); }
.mt-4 { margin-top: var(--spacing-lg); }
.mt-5 { margin-top: var(--spacing-xl); }
